cards.icon:
mdi:lightbulb
{%- if not states("automation.binary_sensor_vic_s_office_occupancy_state_change") | bool(false) -%}
-off
{%- elif area_entities('vic_s_office') | select('match','^light\.') | select('is_state','on') | list | count > 0 -%}
-on
{%- endif -%}
-outline

{% set val = states('sensor.apollo_plt_1b_1743a8_soil_moisture') | float(0) %}
{% if val < 20 %}
  mdi:water-off
{% elif val < 40 %}
  mdi:water-alert
{% elif val < 60 %}
  mdi:water-percent
{% elif val < 80 %}
  mdi:water
{% else %}
  mdi:water-check
{% endif %}

cards.secondary:
Spider Plant Soil Moisture |
{%- set last = states('sensor.apollo_plt_1b_spider_plant_last_online') %}
{% if last not in ['unknown', 'unavailable', 'none', ''] %}
  {% set dt = as_datetime(last) %}
  {% set delta = now() - dt %}
  {% set minutes = (delta.total_seconds() / 60) | int %}

  {% if minutes < 60 %}
    {{ minutes }} minute{{ 's' if minutes != 1 }} ago
  {% elif minutes < 1440 %}
    {{ (minutes / 60) | int }} hour{{ 's' if (minutes / 60) | int != 1 }} ago
  {% else %}
    {{ (minutes / 1440) | int }} day{{ 's' if (minutes / 1440) | int != 1 }} ago
  {% endif %}
{% else %}
  Unknown
{% endif %}

cards.color:
{% set val = states('sensor.apollo_plt_1b_1743a8_soil_moisture') | float(0) %}
{% if val < 20 %}
  red
{% elif val < 40 %}
  orange
{% elif val < 60 %}
  yellow
{% elif val < 80 %}
  limegreen
{% else %}
  deepskyblue
{% endif %}

cards.primary:
{% set next = state_attr('schedule.bedroom_heating_schedule', 'next_event') %}
{% if next %}
  {% set dt = as_datetime(next) %}
  {% set delta = dt - now() %}
  {% set minutes = (delta.total_seconds() / 60) | int %}

  {% if minutes < 60 %}
    In {{ minutes }} minute{{ 's' if minutes != 1 }}
  {% elif minutes < 1440 %}
    In {{ (minutes / 60) | int }} hour{{ 's' if (minutes / 60) | int != 1 }}
  {% else %}
    In {{ (minutes / 1440) | int }} day{{ 's' if (minutes / 1440) | int != 1 }}
  {% endif %}
{% else %}
  No upcoming event
{% endif %}
