custom_fields.content:
[[[
  const hasLabel = states[
    "sensor.[[repo_slug]]_pull_requests"
  ]
    .attributes["[[pr_count_id]]"]
    .labels
    .includes("[[label]]");

  const addRemove = hasLabel ? "Remove" : "Add"
  const icon = hasLabel ? "mdi:label-off" : "mdi:label";
  const label = '[[label]]';

  return `
    <div style="
      display: flex;
      justify-content: space-between;
      max-width: 300px;
      margin: 0 auto;
    ">
      <h4 style="
        margin: 0;
        padding-top: 3px;
      ">${addRemove} <code>${label}</code> label</h4>
      <ha-icon
        icon="${icon}"
        style="
          width: 28px;
          height: 28px;
          margin: 0 0 0 auto;
        "
      ></ha-icon>
    </div>
  `
]]]

