cards.primary:
{% set next = state_attr('schedule.bedroom_heating_schedule', 'next_event') %}
{% if next %}
  {% set dt = as_datetime(next) %}
  {% set delta = dt - now() %}
  {% set minutes = (delta.total_seconds() / 60) | int %}

  {% if minutes < 60 %}
    In {{ minutes }} minute{{ 's' if minutes != 1 }}
  {% elif minutes < 1440 %}
    In {{ (minutes / 60) | int }} hour{{ 's' if (minutes / 60) | int != 1 }}
  {% else %}
    In {{ (minutes / 1440) | int }} day{{ 's' if (minutes / 1440) | int != 1 }}
  {% endif %}
{% else %}
  No upcoming event
{% endif %}

{% set date_str = states('sensor.last_workout_date') %}
{% if date_str not in ['unknown', 'unavailable', 'none', ''] %}
  {% set target = as_datetime(date_str).date() %}
  {% set today = now().date() %}
  {% set delta = (target - today).days %}

  {% if delta == 0 %}
    Today
  {% elif delta == -1 %}
    Yesterday
  {% elif delta == 1 %}
    Tomorrow
  {% elif delta < 0 %}
    {{ (delta | abs) }} days ago
  {% else %}
    In {{ delta }} days
  {% endif %}
{% else %}
  Unknown
{% endif %}

